// Parse the AI response and extract JSON
const aiOutput = $input.first().json.output;
let parsedResponse;

try {
  // Extract JSON from the AI response
  const jsonMatch = aiOutput.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsedResponse = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (error) {
  // Fallback if parsing fails
  parsedResponse = {
    detected_language: 'spanish',
    message_type: 'other',
    requires_salesforce: false,
    response: 'Lo siento, hubo un error procesando tu mensaje.'
  };
}

// Add original message data
parsedResponse.original_message = $('Slack Trigger').first().json.text;
parsedResponse.user_id = $('Slack Trigger').first().json.user;

return parsedResponse;
